  const [isQuickEdit, setIsQuickEdit] = useState(false);
  const [actionFeedback, setActionFeedback] = useState('');

  // „Éï„Ç£„Éº„Éâ„Éê„ÉÉ„ÇØ„É°„ÉÉ„Çª„Éº„Ç∏„ÇíËá™ÂãïÊ∂àÂéª
  React.useEffect(() => {
    if (actionFeedback) {
      const timer = setTimeout(() => setActionFeedback(''), 1500);
      return () => clearTimeout(timer);
    }
  }, [actionFeedback]);

  // „Çø„Çπ„ÇØ„ÅÆÂÆå‰∫Ü/Êú™ÂÆå‰∫ÜÂàá„ÇäÊõø„Åà
  const handleStatusChange = async () => {
    try {
      const newStatus = !task.status;

      const { error: updateError } = await supabase
        .from('tasks')
        .update({ status: newStatus })
        .eq('id', task.id);

      if (updateError) throw updateError;

      // ÊàêÂäü„Éï„Ç£„Éº„Éâ„Éê„ÉÉ„ÇØ
      setActionFeedback(newStatus ? 'üéâ ÂÆå‰∫ÜÔºÅ' : 'üîÑ ÂÜçÈñã');
      refreshTasks();
    } catch (err) {
      console.error('„Çø„Çπ„ÇØ„ÅÆÊõ¥Êñ∞‰∏≠„Å´„Ç®„É©„Éº„ÅåÁô∫Áîü„Åó„Åæ„Åó„Åü:', err);
      setActionFeedback('‚ùå „Ç®„É©„Éº');
    }
  };

  // „Çø„Çπ„ÇØ„ÅÆ‰øùÂ≠òÂá¶ÁêÜ
  const handleSave = async () => {
    setLoading(true);
    setError(null);

    try {
      const { error: updateError } = await supabase
        .from('tasks')
        .update({
          title: editedTask.title,
          description: editedTask.description,
          priority: editedTask.priority,
          due_date: editedTask.due_date,
          category_id: editedTask.category_id
        })
        .eq('id', task.id);

      if (updateError) throw updateError;

      setIsEditing(false);
      setActionFeedback('‚ú® ‰øùÂ≠òÂÆå‰∫Ü');
      refreshTasks();
    } catch (err) {
      console.error('„Çø„Çπ„ÇØ„ÅÆ‰øùÂ≠ò‰∏≠„Å´„Ç®„É©„Éº„ÅåÁô∫Áîü„Åó„Åæ„Åó„Åü:', err);
      setError(err.message);
      setActionFeedback('‚ùå ‰øùÂ≠òÂ§±Êïó');
    } finally {
      setLoading(false);
    }
  };

  // „Çø„Çπ„ÇØ„ÅÆÂâäÈô§Âá¶ÁêÜ
  const handleDelete = async () => {
    if (window.confirm('üóëÔ∏è „Åì„ÅÆ„Çø„Çπ„ÇØ„ÇíÂâäÈô§„Åó„Å¶„ÇÇ„Çà„Çç„Åó„ÅÑ„Åß„Åô„ÅãÔºü')) {
      try {
        const { error: deleteError } = await supabase
          .from('tasks')
          .delete()
          .eq('id', task.id);

        if (deleteError) throw deleteError;

        setActionFeedback('üóëÔ∏è ÂâäÈô§ÂÆå‰∫Ü');
        refreshTasks();
      } catch (err) {
        console.error('„Çø„Çπ„ÇØ„ÅÆÂâäÈô§‰∏≠„Å´„Ç®„É©„Éº„ÅåÁô∫Áîü„Åó„Åæ„Åó„Åü:', err);
        setActionFeedback('‚ùå ÂâäÈô§Â§±Êïó');
      }
    }
  };

  // ÂÑ™ÂÖàÂ∫¶„Å´Âøú„Åò„ÅüË°®Á§∫ÊÉÖÂ†±
  const getPriorityInfo = (priority) => {
    switch (priority) {
      case 'È´ò': return { 
        icon: 'üî•', 
        class: 'priority-high', 
        text: 'Á∑äÊÄ•', 
        bgColor: '#fee2e2',
        textColor: '#dc2626'
      };
      case '‰∏≠': return { 
        icon: '‚öñÔ∏è', 
        class: 'priority-medium', 
        text: 'ÊôÆÈÄö', 
        bgColor: '#fef3c7',
        textColor: '#d97006'
      };
      case '‰Ωé': return { 
        icon: 'üå∏', 
        class: 'priority-low', 
        text: 'Âæå„Åß', 
        bgColor: '#ecfdf5',
        textColor: '#059669'
      };
      default: return { 
        icon: '‚öñÔ∏è', 
        class: 'priority-medium', 
        text: 'ÊôÆÈÄö', 
        bgColor: '#f3f4f6',
        textColor: '#6b7280'
      };
    }
  };

  // ÊúüÈôê„ÅÆÁä∂ÊÖã„ÇíÂèñÂæó
  const getDueDateInfo = (dueDate) => {
    if (!dueDate) return null;
    const due = new Date(dueDate);
    const today = new Date();
    const diffDays = Math.ceil((due - today) / (1000 * 60 * 60 * 24));

    if (diffDays < 0) return { 
      status: 'overdue', 
      text: `${Math.abs(diffDays)}Êó•ÈÅÖ„Çå`, 
      icon: 'üö®',
      bgColor: '#fecaca',
      textColor: '#dc2626'
    };
    if (diffDays === 0) return { 
      status: 'today', 
      text: '‰ªäÊó•', 
      icon: '‚ö°',
      bgColor: '#fbbf24',
      textColor: '#ffffff'
    };
    if (diffDays === 1) return { 
      status: 'tomorrow', 
      text: 'ÊòéÊó•', 
      icon: '‚è∞',
      bgColor: '#fed7aa',
      textColor: '#ea580c'
    };
    if (diffDays <= 7) return { 
      status: 'week', 
      text: `${diffDays}Êó•Âæå`, 
      icon: 'üìÖ',
      bgColor: '#dbeafe',
      textColor: '#2563eb'
    };
    return { 
      status: 'future', 
      text: due.toLocaleDateString(), 
      icon: 'üìÜ',
      bgColor: '#e5e7eb',
      textColor: '#6b7280'
    };
  };

  // Á¥†Êó©„ÅÑ„Çø„Ç§„Éà„É´Á∑®ÈõÜ
  const handleQuickTitleEdit = async (newTitle) => {
    if (!newTitle.trim() || newTitle === task.title) {
      setIsQuickEdit(false);
      return;
    }

    try {
      const { error } = await supabase
        .from('tasks')
        .update({ title: newTitle.trim() })
        .eq('id', task.id);

      if (error) throw error;
      setActionFeedback('‚úèÔ∏è Êõ¥Êñ∞ÂÆå‰∫Ü');
      refreshTasks();
    } catch (err) {
      console.error('„Çø„Ç§„Éà„É´Êõ¥Êñ∞„Ç®„É©„Éº:', err);
      setActionFeedback('‚ùå Êõ¥Êñ∞Â§±Êïó');
    }
    setIsQuickEdit(false);
  };

  // ÂÑ™ÂÖàÂ∫¶„ÇíÁ¥†Êó©„ÅèÂ§âÊõ¥
  const cyclePriority = async () => {
    const priorities = ['‰Ωé', '‰∏≠', 'È´ò'];
    const currentIndex = priorities.indexOf(task.priority || '‰∏≠');
    const newPriority = priorities[(currentIndex + 1) % priorities.length];

    try {
      const { error } = await supabase
        .from('tasks')
        .update({ priority: newPriority })
        .eq('id', task.id);

      if (error) throw error;
      setActionFeedback(`üîÑ ${newPriority}„Å´Â§âÊõ¥`);
      refreshTasks();
    } catch (err) {
      console.error('ÂÑ™ÂÖàÂ∫¶Êõ¥Êñ∞„Ç®„É©„Éº:', err);
      setActionFeedback('‚ùå Êõ¥Êñ∞Â§±Êïó');
    }
  };

  // Á∑®ÈõÜ„É¢„Éº„Éâ„ÅÆË°®Á§∫
  if (isEditing) {
    return (
      <div className="task-item-edit">
        {error && (
          <div className="error-message">
            ‚ùå {error}
          </div>
        )}

        <div className="edit-form">
          <div className="form-field">
            <label htmlFor="title">üìù „Çø„Ç§„Éà„É´</label>
            <input
              id="title"
              type="text"
              value={editedTask.title}
              onChange={(e) => setEditedTask({ ...editedTask, title: e.target.value })}
              placeholder="‰Ωï„Çí„Åó„Åæ„Åô„ÅãÔºü"
              autoFocus
              required
            />
          </div>

          <div className="form-field">
            <label htmlFor="description">üìÑ Ë™¨ÊòéÔºà‰ªªÊÑèÔºâ</label>
            <textarea
              id="description"
              value={editedTask.description || ''}
              onChange={(e) => setEditedTask({ ...editedTask, description: e.target.value })}
              placeholder="Ë©≥Á¥∞ÊÉÖÂ†±„Åå„ÅÇ„Çå„Å∞..."
              rows="3"
            />
          </div>

          <div className="form-row">
            <div className="form-field">
              <label htmlFor="priority">üéØ ÂÑ™ÂÖàÂ∫¶</label>
              <select
                id="priority"
                value={editedTask.priority || '‰∏≠'}
                onChange={(e) => setEditedTask({ ...editedTask, priority: e.target.value })}
              >
                <option value="È´ò">üî• ‰ªä„Åô„Åê„ÇÑ„Çã</option>
                <option value="‰∏≠">‚öñÔ∏è ÊôÆÈÄö</option>
                <option value="‰Ωé">üå∏ Âæå„Åß</option>
              </select>
            </div>

            <div className="form-field">
              <label htmlFor="due_date">üìÖ „ÅÑ„Å§„Åæ„ÅßÔºü</label>
              <input
                id="due_date"
                type="date"
                value={editedTask.due_date || ''}
                onChange={(e) => setEditedTask({ ...editedTask, due_date: e.target.value })}
              />
            </div>
          </div>

          <div className="form-field">
            <label htmlFor="category">üè∑Ô∏è „Ç´„ÉÜ„Ç¥„É™</label>
            <select
              id="category"
              value={editedTask.category_id || ''}
              onChange={(e) => setEditedTask({ ...editedTask, category_id: e.target.value })}
            >
              <option value="">„Ç´„ÉÜ„Ç¥„É™„Å™„Åó</option>
              {categories.map((category) => (
                <option key={category.id} value={category.id}>
                  {category.name}
                </option>
              ))}
            </select>
          </div>
        </div>

        <div className="edit-actions">
          <button
            onClick={() => setIsEditing(false)}
            className="cancel-btn"
          >
            ‚ùå „Ç≠„É£„É≥„Çª„É´
          </button>
          <button
            onClick={handleSave}
            className="save-btn"
            disabled={loading || !editedTask.title.trim()}
          >
            {loading ? 'üíæ ‰øùÂ≠ò‰∏≠...' : '‚ú® ‰øùÂ≠ò'}
          </button>
        </div>
      </div>
    );
  }

  const priorityInfo = getPriorityInfo(task.priority);
  const dueDateInfo = getDueDateInfo(task.due_date);

  // ÈÄöÂ∏∏„É¢„Éº„Éâ„ÅÆË°®Á§∫ÔºàADHDÊúÄÈÅ©ÂåñÔºâ
  return (
    <div className={`task-card ${priorityInfo.class} ${task.status ? 'completed' : ''} ${isHighlighted ? 'highlighted' : ''} ${dueDateInfo?.status || ''}`}>
      {/* „Ç¢„ÇØ„Ç∑„Éß„É≥„Éï„Ç£„Éº„Éâ„Éê„ÉÉ„ÇØ */}
      {actionFeedback && (
        <div className="action-feedback">
          {actionFeedback}
        </div>
      )}

      {/* Â∑¶ÂÅ¥Ôºö„Çπ„ÉÜ„Éº„Çø„Çπ„Å®„Éó„É©„Ç§„Ç™„É™„ÉÜ„Ç£ */}
      <div className="task-left-section">
        <button
          className={`completion-btn ${task.status ? 'completed' : ''}`}
          onClick={handleStatusChange}
          title={task.status ? '„ÇØ„É™„ÉÉ„ÇØ„ÅßÊú™ÂÆå‰∫Ü„Å´Êàª„Åô' : '„ÇØ„É™„ÉÉ„ÇØ„ÅßÂÆå‰∫ÜÔºÅ'}
        >
          <div className="completion-icon">
            {task.status ? '‚úÖ' : '‚≠ï'}
          </div>
        </button>

        <button
          className="priority-btn"
          onClick={cyclePriority}
          title={`ÂÑ™ÂÖàÂ∫¶: ${priorityInfo.text} („ÇØ„É™„ÉÉ„ÇØ„ÅßÂàá„ÇäÊõø„Åà)`}
          style={{
            backgroundColor: priorityInfo.bgColor,
            color: priorityInfo.textColor
          }}
        >
          {priorityInfo.icon}
        </button>
      </div>

      {/* ‰∏≠Â§ÆÔºö„É°„Ç§„É≥„Ç≥„É≥„ÉÜ„É≥„ÉÑ */}
      <div className="task-content">
        <div className="task-header">
          {isQuickEdit ? (
            <input
              type="text"
              defaultValue={task.title}
              autoFocus
              onBlur={(e) => handleQuickTitleEdit(e.target.value)}
              onKeyPress={(e) => {
                if (e.key === 'Enter') {
                  handleQuickTitleEdit(e.target.value);
                }
              }}
              onKeyDown={(e) => {
                if (e.key === 'Escape') {
                  setIsQuickEdit(false);
                }
              }}
              className="quick-title-edit"
            />
          ) : (
            <h3 
              className={`task-title ${task.status ? 'completed' : ''}`}
              onClick={() => setIsQuickEdit(true)}
              title="„ÇØ„É™„ÉÉ„ÇØ„Åß„Çø„Ç§„Éà„É´Á∑®ÈõÜ"
            >
              {task.title}
            </h3>
          )}
        </div>

        <div className="task-meta">
          {task.categories && (
            <span 
              className="category-tag"
              style={{ 
                backgroundColor: task.categories.color + '20',
                color: task.categories.color,
                borderColor: task.categories.color + '50'
              }}
            >
              üìÅ {task.categories.name}
            </span>
          )}

          {dueDateInfo && (
            <span 
              className={`due-date-tag ${dueDateInfo.status}`}
              style={{
                backgroundColor: dueDateInfo.bgColor,
                color: dueDateInfo.textColor
              }}
            >
              {dueDateInfo.icon} {dueDateInfo.text}
            </span>
          )}
        </div>

        {task.description && !task.status && (
          <p className="task-description">
            {task.description}
          </p>
        )}
      </div>

      {/* Âè≥ÂÅ¥Ôºö„Ç¢„ÇØ„Ç∑„Éß„É≥ */}
      <div className="task-actions">
        <button
          onClick={() => setIsEditing(true)}
          className="action-btn edit-btn"
          title="Ë©≥Á¥∞Á∑®ÈõÜ"
        >
          ‚úèÔ∏è
        </button>
        <button
          onClick={handleDelete}
          className="action-btn delete-btn"
          title="ÂâäÈô§"
        >
          üóëÔ∏è
        </button>
      </div>
    </div>
  );
}

export default TaskItem;
