import React, { useState, useRef, useEffect } from 'react';
import supabase from '../../services/supabaseClient';
import './Task.css';

function QuickTaskInput({ categories, onTaskAdded }) {
  const [isExpanded, setIsExpanded] = useState(false);
  const [newTask, setNewTask] = useState({
    title: '',
    priority: '‰∏≠',
    due_date: '',
    category_id: ''
  });
  const [loading, setLoading] = useState(false);
  const [successMessage, setSuccessMessage] = useState('');
  const inputRef = useRef(null);

  // ÊàêÂäü„É°„ÉÉ„Çª„Éº„Ç∏„ÇíËá™ÂãïÊ∂àÂéª
  useEffect(() => {
    if (successMessage) {
      const timer = setTimeout(() => setSuccessMessage(''), 2000);
      return () => clearTimeout(timer);
    }
  }, [successMessage]);

  // „Éï„Ç©„Éº„Ç´„ÇπÊôÇ„Å´Â±ïÈñã
  const handleFocus = () => {
    setIsExpanded(true);
  };

  // Enter„Ç≠„Éº„Åß„ÅÆÁ¥†Êó©„ÅÑËøΩÂä†
  const handleKeyPress = async (e) => {
    if (e.key === 'Enter' && !e.shiftKey && newTask.title.trim()) {
      e.preventDefault();
      await handleQuickAdd();
    }
  };

  // Escape„Ç≠„Éº„ÅßÊäò„Çä„Åü„Åü„Åø
  const handleKeyDown = (e) => {
    if (e.key === 'Escape') {
      setIsExpanded(false);
      setNewTask({ title: '', priority: '‰∏≠', due_date: '', category_id: '' });
      inputRef.current?.blur();
    }
  };

  // Á¥†Êó©„ÅÑ„Çø„Çπ„ÇØËøΩÂä†
  const handleQuickAdd = async () => {
    if (!newTask.title.trim()) return;

    setLoading(true);
    try {
      const { error } = await supabase
        .from('tasks')
        .insert([{
          title: newTask.title.trim(),
          priority: newTask.priority,
          due_date: newTask.due_date || null,
          category_id: newTask.category_id || null,
          status: false
        }]);

      if (error) throw error;

      // ÊàêÂäü„Éï„Ç£„Éº„Éâ„Éê„ÉÉ„ÇØ
      setSuccessMessage(`‚ú® "${newTask.title}" „ÇíËøΩÂä†„Åó„Åæ„Åó„ÅüÔºÅ`);

      // „É™„Çª„ÉÉ„Éà
      setNewTask({ title: '', priority: '‰∏≠', due_date: '', category_id: '' });
      setIsExpanded(false);
      onTaskAdded();

      // „Éï„Ç©„Éº„Ç´„Çπ„ÇíÊàª„ÅôÔºàÁ∂ôÁ∂öÁöÑ„Å™ÂÖ•Âäõ„Çí„Çµ„Éù„Éº„ÉàÔºâ
      setTimeout(() => inputRef.current?.focus(), 100);
    } catch (err) {
      console.error('„Çø„Çπ„ÇØ„ÅÆËøΩÂä†‰∏≠„Å´„Ç®„É©„Éº„ÅåÁô∫Áîü„Åó„Åæ„Åó„Åü:', err);
      setSuccessMessage('‚ùå „Ç®„É©„Éº„ÅåÁô∫Áîü„Åó„Åæ„Åó„Åü');
    } finally {
      setLoading(false);
    }
  };

  // „ÇØ„Ç§„ÉÉ„ÇØËøΩÂä†„Éú„Çø„É≥ÔºàÂÑ™ÂÖàÂ∫¶Âà•Ôºâ
  const handleQuickAddWithPriority = async (priority, emoji) => {
    if (!newTask.title.trim()) return;

    const taskWithPriority = { ...newTask, priority };
    setNewTask(taskWithPriority);

    // Â∞ë„ÅóÂæÖ„Å£„Å¶„Åã„ÇâËøΩÂä†ÔºàË¶ñË¶öÁöÑ„Éï„Ç£„Éº„Éâ„Éê„ÉÉ„ÇØÔºâ
    setTimeout(() => {
      handleQuickAdd();
    }, 100);
  };

  // Â§ñÂÅ¥„ÇØ„É™„ÉÉ„ÇØ„ÅßÊäò„Çä„Åü„Åü„Åø
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (inputRef.current && !inputRef.current.closest('.quick-input-container').contains(event.target)) {
        if (!newTask.title.trim()) {
          setIsExpanded(false);
        }
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, [newTask.title]);

  return (
    <div className={`quick-input-container ${isExpanded ? 'expanded' : ''}`}>
      {/* ÊàêÂäü„É°„ÉÉ„Çª„Éº„Ç∏ */}
      {successMessage && (
        <div className="success-message">
          {successMessage}
        </div>
      )}

      {/* „É°„Ç§„É≥ÂÖ•ÂäõÊ¨Ñ */}
      <div className="main-input-row">
        <div className="input-wrapper">
          <input
            ref={inputRef}
            type="text"
            value={newTask.title}
            onChange={(e) => setNewTask({ ...newTask, title: e.target.value })}
            onFocus={handleFocus}
            onKeyPress={handleKeyPress}
            onKeyDown={handleKeyDown}
            placeholder={isExpanded ? "‰ªä„ÇÑ„Çã„Åπ„Åç„Åì„Å®„ÅØÔºü (Enter „ÅßËøΩÂä†)" : "‚ú® Êñ∞„Åó„ÅÑ„Çø„Çπ„ÇØ„ÇíÂÖ•Âäõ... („Åô„Åê„Å´Âßã„ÇÅ„Çâ„Çå„Åæ„Åô)"}
            className={`quick-input ${loading ? 'loading' : ''}`}
            disabled={loading}
            autoComplete="off"
          />
          {loading && <div className="input-spinner">‚è≥</div>}
        </div>

        {/* Ë∂ÖÈ´òÈÄüËøΩÂä†„Éú„Çø„É≥ */}
        {isExpanded && newTask.title.trim() && (
          <div className="quick-action-buttons">
            <button
              onClick={() => handleQuickAddWithPriority('È´ò', 'üî•')}
              disabled={loading}
              className="priority-quick-btn high"
              title="Á∑äÊÄ•„Å®„Åó„Å¶ËøΩÂä†"
            >
              üî•
            </button>
            <button
              onClick={handleQuickAdd}
              disabled={loading}
              className="quick-add-btn main"
              title="ËøΩÂä†"
            >
              ‚ûï
            </button>
            <button
              onClick={() => handleQuickAddWithPriority('‰Ωé', 'üå∏')}
              disabled={loading}
              className="priority-quick-btn low"
              title="Âæå„Åß„ÇÑ„Çã„Å®„Åó„Å¶ËøΩÂä†"
            >
              üå∏
            </button>
          </div>
        )}
      </div>

      {/* Êã°Âºµ„Ç™„Éó„Ç∑„Éß„É≥ÔºàÂøÖË¶ÅÊôÇ„ÅÆ„ÅøË°®Á§∫Ôºâ */}
      {isExpanded && (
        <div className="expanded-options">
          <div className="quick-options-row">
            {/* ÂÑ™ÂÖàÂ∫¶ */}
            <div className="quick-option">
              <label>üéØ ÂÑ™ÂÖàÂ∫¶</label>
              <select
                value={newTask.priority}
                onChange={(e) => setNewTask({ ...newTask, priority: e.target.value })}
                className="priority-select"
              >
                <option value="È´ò">üî• ‰ªä„Åô„Åê„ÇÑ„Çã</option>
                <option value="‰∏≠">‚öñÔ∏è ÊôÆÈÄö</option>
                <option value="‰Ωé">üå∏ Âæå„Åß</option>
              </select>
            </div>

            {/* ÊúüÈôê */}
            <div className="quick-option">
              <label>üìÖ „ÅÑ„Å§„Åæ„ÅßÔºü</label>
              <input
                type="date"
                value={newTask.due_date}
                onChange={(e) => setNewTask({ ...newTask, due_date: e.target.value })}
                className="date-input"
              />
            </div>

            {/* „Ç´„ÉÜ„Ç¥„É™ */}
            <div className="quick-option">
              <label>üè∑Ô∏è „Ç´„ÉÜ„Ç¥„É™</label>
              <select
                value={newTask.category_id}
                onChange={(e) => setNewTask({ ...newTask, category_id: e.target.value })}
                className="category-select"
              >
                <option value="">„Å™„Åó</option>
                {categories.map((category) => (
                  <option key={category.id} value={category.id}>
                    {category.name}
                  </option>
                ))}
              </select>
            </div>
          </div>

          {/* „Éí„É≥„Éà„Å®„Ç∑„Éß„Éº„Éà„Ç´„ÉÉ„Éà */}
          <div className="quick-tips">
            <div className="tips-row">
              <span className="tip">‚ö° <kbd>Enter</kbd> „ÅßÁû¨ÊôÇËøΩÂä†</span>
              <span className="tip">üî• „ÇØ„É™„ÉÉ„ÇØ„ÅßÁ∑äÊÄ•ËøΩÂä†</span>
              <span className="tip">üå∏ „ÇØ„É™„ÉÉ„ÇØ„ÅßÂæåÂõû„ÅóËøΩÂä†</span>
              <span className="tip">‚≠ï <kbd>Esc</kbd> „ÅßÈñâ„Åò„Çã</span>
            </div>
          </div>
        </div>
      )}

      {/* Âä±„Åæ„Åó„É°„ÉÉ„Çª„Éº„Ç∏ */}
      {!isExpanded && (
        <div className="motivation-message">
          üí™ Â∞è„Åï„Å™‰∏ÄÊ≠©„Åã„ÇâÂßã„ÇÅ„Çà„ÅÜÔºÅ‰Ωï„Åß„ÇÇÊõ∏„ÅÑ„Å¶OK
        </div>
      )}
    </div>
  );
}

export default QuickTaskInput;
