import React, { useState } from 'react';
import TaskItem from './TaskItem';
import QuickTaskInput from './QuickTaskInput';
import './Task.css';

function TaskList({ tasks, categories, loading, refreshTasks }) {
  const [draggedTask, setDraggedTask] = useState(null);
  const [showCompleted, setShowCompleted] = useState(true);

  // „Çø„Çπ„ÇØ„ÇíÂÑ™ÂÖàÂ∫¶„Å®ÊúüÈôê„Åß„ÇΩ„Éº„Éà
  const sortTasks = (tasks) => {
    return [...tasks].sort((a, b) => {
      // ÂÆå‰∫ÜÊ∏à„Åø„ÅØ‰∏ã„Å´
      if (a.status !== b.status) {
        return a.status ? 1 : -1;
      }

      // ÂÑ™ÂÖàÂ∫¶„Åß„ÇΩ„Éº„Éà (È´ò > ‰∏≠ > ‰Ωé)
      const priorityOrder = { 'È´ò': 3, '‰∏≠': 2, '‰Ωé': 1 };
      const priorityDiff = (priorityOrder[b.priority] || 2) - (priorityOrder[a.priority] || 2);

      if (priorityDiff !== 0) return priorityDiff;

      // ÊúüÈôê„Åß„ÇΩ„Éº„Éà
      if (a.due_date && b.due_date) {
        return new Date(a.due_date) - new Date(b.due_date);
      }
      if (a.due_date && !b.due_date) return -1;
      if (!a.due_date && b.due_date) return 1;

      // ÊúÄÂæå„Å´‰ΩúÊàêÊó•È†Ü
      return new Date(b.created_at) - new Date(a.created_at);
    });
  };

  const sortedTasks = sortTasks(tasks);
  const activeTasks = sortedTasks.filter(task => !task.status);
  const completedTasks = sortedTasks.filter(task => task.status);
  const todaysTasks = activeTasks.filter(task => {
    if (!task.due_date) return false;
    const today = new Date().toDateString();
    const taskDate = new Date(task.due_date).toDateString();
    return today === taskDate;
  });

  if (loading) {
    return (
      <div className="task-container">
        <QuickTaskInput categories={categories} onTaskAdded={refreshTasks} />
        <div className="task-loading">
          <div className="loading-spinner">‚è≥</div>
          <p>„Çø„Çπ„ÇØ„ÇíË™≠„ÅøËæº„Çì„Åß„ÅÑ„Åæ„Åô...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="task-container">
      {/* „ÇØ„Ç§„ÉÉ„ÇØÂÖ•Âäõ„Ç®„É™„Ç¢ - ÊúÄÂÑ™ÂÖàË°®Á§∫ */}
      <QuickTaskInput categories={categories} onTaskAdded={refreshTasks} />

      {/* Áµ±Ë®à„Å®„É¢„ÉÅ„Éô„Éº„Ç∑„Éß„É≥ */}
      <div className="dashboard-stats">
        <div className="stats-grid">
          <div className="stat-card urgent">
            <div className="stat-icon">üî•</div>
            <div className="stat-content">
              <div className="stat-number">{activeTasks.filter(t => t.priority === 'È´ò').length}</div>
              <div className="stat-label">Á∑äÊÄ•</div>
            </div>
          </div>

          <div className="stat-card today">
            <div className="stat-icon">üìÖ</div>
            <div className="stat-content">
              <div className="stat-number">{todaysTasks.length}</div>
              <div className="stat-label">‰ªäÊó•</div>
            </div>
          </div>

          <div className="stat-card progress">
            <div className="stat-icon">‚ú®</div>
            <div className="stat-content">
              <div className="stat-number">
                {tasks.length > 0 ? Math.round((completedTasks.length / tasks.length) * 100) : 0}%
              </div>
              <div className="stat-label">ÂÆå‰∫ÜÁéá</div>
            </div>
          </div>

          <div className="stat-card total">
            <div className="stat-icon">üìã</div>
            <div className="stat-content">
              <div className="stat-number">{activeTasks.length}</div>
              <div className="stat-label">ÊÆã„Çä</div>
            </div>
          </div>
        </div>

        {/* Âä±„Åæ„Åó„É°„ÉÉ„Çª„Éº„Ç∏ */}
        <div className="motivation-banner">
          {activeTasks.length === 0 ? (
            <div className="motivation-success">
              üéâ Á¥†Êô¥„Çâ„Åó„ÅÑÔºÅÂÖ®„Å¶„ÅÆ„Çø„Çπ„ÇØ„ÅåÂÆå‰∫Ü„Åó„Å¶„ÅÑ„Åæ„ÅôÔºÅ
            </div>
          ) : activeTasks.length <= 3 ? (
            <div className="motivation-encourage">
              üí™ „ÅÇ„Å®{activeTasks.length}ÂÄãÔºÅ„ÇÇ„ÅÜÂ∞ë„Åó„ÅßÂÖ®ÈÉ®ÂÆå‰∫Ü„Åß„ÅôÔºÅ
            </div>
          ) : (
            <div className="motivation-start">
              üöÄ ‰∏Ä„Å§„Åö„Å§Áâá‰ªò„Åë„Å¶„ÅÑ„Åç„Åæ„Åó„Çá„ÅÜÔºÅ
            </div>
          )}
        </div>
      </div>

      {/* „Çø„Çπ„ÇØ‰∏ÄË¶ß */}
      <div className="task-list">
        {tasks.length === 0 ? (
          <div className="empty-state">
            <div className="empty-icon">üéØ</div>
            <h3>ÊúÄÂàù„ÅÆ„Çø„Çπ„ÇØ„ÇíËøΩÂä†„Åó„Å¶„Åø„Åæ„Åó„Çá„ÅÜÔºÅ</h3>
            <p>‰∏ä„ÅÆÂÖ•ÂäõÊ¨Ñ„Å´‰Ωï„Åß„ÇÇÊõ∏„ÅÑ„Å¶Enter„ÇíÊäº„Åô„Å†„Åë</p>
            <div className="empty-suggestions">
              <div className="suggestion-item">üí° „Äå„É°„Éº„É´„ÇíÁ¢∫Ë™ç„Åô„Çã„Äç</div>
              <div className="suggestion-item">üí° „ÄåÈÉ®Â±ã„ÇíÁâá‰ªò„Åë„Çã„Äç</div>
              <div className="suggestion-item">üí° „ÄåË≤∑„ÅÑÁâ©„É™„Çπ„Éà„Çí‰Ωú„Çã„Äç</div>
            </div>
          </div>
        ) : (
          <>
            {/* ‰ªäÊó•„ÅÆ„Çø„Çπ„ÇØ */}
            {todaysTasks.length > 0 && (
              <div className="task-section">
                <h3 className="section-title today-title">
                  üìÖ ‰ªäÊó•„ÇÑ„Çã„Åì„Å® ({todaysTasks.length})
                </h3>
                <div className="task-grid">
                  {todaysTasks.map((task) => (
                    <TaskItem
                      key={task.id}
                      task={task}
                      categories={categories}
                      refreshTasks={refreshTasks}
                      isHighlighted={true}
                    />
                  ))}
                </div>
              </div>
            )}

            {/* Á∑äÊÄ•„Çø„Çπ„ÇØ */}
            {activeTasks.filter(t => t.priority === 'È´ò' && !todaysTasks.includes(t)).length > 0 && (
              <div className="task-section">
                <h3 className="section-title urgent-title">
                  üî• Á∑äÊÄ•„ÉªÈáçË¶Å ({activeTasks.filter(t => t.priority === 'È´ò' && !todaysTasks.includes(t)).length})
                </h3>
                <div className="task-grid">
                  {activeTasks
                    .filter(t => t.priority === 'È´ò' && !todaysTasks.includes(t))
                    .map((task) => (
                      <TaskItem
                        key={task.id}
                        task={task}
                        categories={categories}
                        refreshTasks={refreshTasks}
                      />
                    ))}
                </div>
              </div>
            )}

            {/* ÈÄöÂ∏∏„ÅÆ„Çø„Çπ„ÇØ */}
            {activeTasks.filter(t => t.priority !== 'È´ò' && !todaysTasks.includes(t)).length > 0 && (
              <div className="task-section">
                <h3 className="section-title normal-title">
                  üìã „Åù„ÅÆ‰ªñ„ÅÆ„Çø„Çπ„ÇØ ({activeTasks.filter(t => t.priority !== 'È´ò' && !todaysTasks.includes(t)).length})
                </h3>
                <div className="task-grid">
                  {activeTasks
                    .filter(t => t.priority !== 'È´ò' && !todaysTasks.includes(t))
                    .map((task) => (
                      <TaskItem
                        key={task.id}
                        task={task}
                        categories={categories}
                        refreshTasks={refreshTasks}
                      />
                    ))}
                </div>
              </div>
            )}

            {/* ÂÆå‰∫ÜÊ∏à„Åø„Çø„Çπ„ÇØ */}
            {completedTasks.length > 0 && (
              <div className="task-section">
                <div className="section-header">
                  <h3 className="section-title completed-title">
                    ‚úÖ ÂÆå‰∫ÜÊ∏à„Åø ({completedTasks.length})
                  </h3>
                  <button
                    onClick={() => setShowCompleted(!showCompleted)}
                    className="toggle-completed-btn"
                  >
                    {showCompleted ? 'üôà Èö†„Åô' : 'üëÄ Ë°®Á§∫'}
                  </button>
                </div>

                {showCompleted && (
                  <div className="task-grid completed-grid">
                    {completedTasks.slice(0, 10).map((task) => (
                      <TaskItem
                        key={task.id}
                        task={task}
                        categories={categories}
                        refreshTasks={refreshTasks}
                        isCompleted={true}
                      />
                    ))}
                    {completedTasks.length > 10 && (
                      <div className="more-completed">
                        +{completedTasks.length - 10} ÂÄã„ÅÆÂÆå‰∫ÜÊ∏à„Åø„Çø„Çπ„ÇØ
                      </div>
                    )}
                  </div>
                )}
              </div>
            )}
          </>
        )}
      </div>
    </div>
  );
}

export default TaskList;
